import React, { Component } from "react";
import axios from "axios";
class UpdateProjectTask extends Component{
  constructor(props) {
    super(props);
    // console.dir(props);
    this.state = {
      pt_id: props.location.id,
      summary:"",
      acceptanceCriteria:"",
      status:""
    }
    // console.log(this.state.pt_id);

  }
  componentDidMount() {

    const res= axios
    .get("http://localhost:8080/api/board/"+this.state.pt_id)
    .then(
      response => { 
        this.setState({ project_tasks: response.data });
        console.log(this.state.project_tasks);
        }
      );


  }


    render(){
      const {project_tasks} = this.state;

      return(

<div>{project_tasks.summary}
  <div className="addProjectTask">
    <div className="container">
      <div className="row">
        <div className="col-md-8 m-auto">
          <a href="/RSApp" className="btn btn-light">
            Back to Board
          </a>
          <h4 className="display-4 text-center">Add /Update Project Task</h4>
          <form>
            <div className="form-group">
              <input type="text" className="form-control form-control-lg" name="summary" placeholder="Project Task summary" value={this.state.project_tasks.summary}/>

            </div>
            <div className="form-group">
              <textarea className="form-control form-control-lg" placeholder="Acceptance Criteria" name="acceptanceCriteria" defaultValue={""} />
            </div>
            <div className="form-group">
              <select className="form-control form-control-lg" name="status">
                <option value>Select Status</option>
                <option value="TO_DO">TO DO</option>
                <option value="IN_PROGRESS">IN PROGRESS</option>
                <option value="DONE">DONE</option>
              </select>
            </div>
            <input type="submit" className="btn btn-primary btn-block mt-4" />
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
      )
    }
}
export default UpdateProjectTask
