const SET_STATUS = "SET_STATUS";
export function action(email, password) {
  return dispatch => {
    callLoginApi(email, password, error => {
      dispatch(setLoginSuccess(error));
      // if (!error) {
      //   dispatch(setLoginSuccess(error));
      // } else {
      //   dispatch(setLoginError(error));
      // }
    });
  };
}

function setLoginSuccess(loginstatus) {
  return {
    type: SET_STATUS,
    loginstatus
  };
}
// function setLoginError(loginerror) {
//   return {
//     type: SET_STATUS,
//     loginerror
//   };
// }
function callLoginApi(email, password, callback) {
  setTimeout(() => {
    if (email === "a@a.com" && password === "aa") {
      return callback("success");
    } else {
      return callback("Invalid email and password");
    }
  }, 1000);
}
