const SET_STATUS = "SET_STATUS";
export function action(email, password) {
  return dispatch => {
    callLoginApi(email, password, status => {
      if (status !== true) {
        dispatch(setLoginStatus("success", true));
      } else {
        dispatch(setLoginStatus("Invalid username or password", false));
      }
    });
  };
}

function setLoginStatus(loginstatus, flag) {
  return {
    type: SET_STATUS,
    loginstatus,
    flag
  };
}
// function setLoginError(loginerror) {
//   return {
//     type: SET_STATUS,
//     loginerror
//   };
// }
function callLoginApi(email, password, callback) {
  setTimeout(() => {
    if (email === "a@a.com" && password === "aa") {
      return callback(true);
    } else {
      return callback(false);
    }
  }, 1000);
}
